//JsonSluper: is a class that parses JSON text content into groovy objects such as 
//maps, lists

//It has some methods parse: parseText, parseFile

import groovy.json.JsonSlurper

testRunner.runTestStepByName('GET-Get users')
def res = context.expand('${GET-Get users#Response}')

//Parse response
def obj = new JsonSlurper().parseText(res)
log.info obj.page
log.info obj.per_page
log.info obj.data[0].first_name

def testStep = testRunner.testCase.testSteps["Properties"]

if(testStep.getPropertyCount()>0){
	for(prop in testStep.getPropertyList()){
		log.info ("This property step has property: " +prop.getName() +
		" with value: " + prop.getValue())
	}
}

//count of test cases in a testsuite
