con:config.script:
import groovy.util.* import groovy.json.JsonSlurper
def res, obj def groovyUtils = new com.eviware.soapui.support.GroovyUtils(context) def projectDir = groovyUtils.projectPath File file = new File(projectDir) def firstLevelDir = file.getParent()
def filePath = firstLevelDir + "\\SoapUI\\User.csv" context.fileReader = new BufferedReader(new FileReader(filePath))
rows = context.fileReader.readLines() int n = rows.size()
for(int i = 1; i < n; i++){ row = rows[i] String[] data = row.split(",") //Mike, QA
//Set the property value testRunner.testCase.getTestStepByName("Properties").setPropertyValue("Name", data[0]) testRunner.testCase.getTestStepByName("Properties").setPropertyValue("Role", data[1])
//run the step testRunner.runTestStepByName("POST-CreateUser")
res = context.expand('${POST-CreateUser#Response}') obj = new JsonSlurper().parseText(res)
assert obj.name == data[0] assert obj.job == data[1] }

con:testSuite.con:setupScript:
import com.google.common.collect.*;

// Get a list of the test cases in the suite and partition them into batches of 10 using Google Guava
def testCases = context.getTestSuite().getTestCaseList();
def List<List<com.eviware.soapui.model.testsuite.TestCase>> testcasePartitions = Lists.partition(testCases, 3);

// Loop through the partitions
for (partition in testcasePartitions) {

    // Loop through the test cases in each partition
    for (testcase in partition) {

        if (!testcase.isDisabled()) {
            log.info("Running tests case " + testcase.getName());           

            def properties = new com.eviware.soapui.support.types.StringToObjectMap ()
            testcase.run(properties, false)         

            // Check the outcomes here if required.
        }
    }
}
